sys_script_include.script:
function GlideRecordSetValue(grRecord,strField,strValue)
{
	if (IsValidRecord(grRecord) == false)
	{
		return false;
	}
	if (strField == 'sys_id')
	{
		if (grRecord.isNewRecord() != true)
		{
			return false;
		}
		RunScriptInGlobalScope('param1.setNewGuidValue(param2);',grRecord,strValue);
	}
	else
	{
		RunScriptInGlobalScope('param1.setValue(param2,param3);',grRecord,strField,strValue);
	}
	return true;
}
