function IsEqual(vValue1, vValue2, bIgnoreOrder)
{
	if (Array.isArray(vValue1) && Array.isArray(vValue2))
	{
		if (vValue1.length !== vValue2.length)
		{
			return false;
		}
		if (bIgnoreOrder == true)
		{
			vValue1 = vValue1.sort();
			vValue2 = vValue2.sort();
		}
		return vValue1.every(function(value, index)
		{
			return IsEqual(value,vValue2[index]);
		});
	}

	if (IsObject(vValue1) && IsObject(vValue2))
	{
		//var aKeys1 = Object.keys(vValue1);
		//var aKeys2 = Object.keys(vValue2);
		/*
		if (aKeys1.length !== aKeys2.length)
		{
			return false;
		}*/
/*
		if (bIgnoreOrder == true)
		{
			aKeys1 = aKeys1.sort();
			aKeys2 = aKeys2.sort();
		}*/
		/*
		aKeys1.forEach( function(strKey)
		{
			if (IsEqual(vValue1[strKey],vValue2[strKey]) == false)
			{
				return false;
			}
		});*/	
		//return true;
	}

	return vValue1 === vValue2;
}
