sys_script_include.script:
function GetRelatedRecordsList(grRecord,bApplicationFilesOnly,nMaxLevel)
{
	var tree = GetRelatedRecordsTree(grRecord,bApplicationFilesOnly,nMaxLevel);
	if (tree === false)
	{
		return false;
	}

	var aAllRelatedRecords = [];

	var record = Clone(tree);
	delete record._related_records;
	aAllRelatedRecords.push(record);

	function AddRelatedRecords(record)
	{
		var aRelatedRecords = GetArrayValue(GetValue(record,'_related_records'));
		for (var nRelatedRecord = 0; nRelatedRecord < aRelatedRecords.length; nRelatedRecord++)
		{
			var related_record = aRelatedRecords[nRelatedRecord];
			AddRelatedRecords(related_record)
			delete related_record._related_records;
			aAllRelatedRecords.push(related_record);
		}
	}
	AddRelatedRecords(tree);

	return aAllRelatedRecords;
}
