sys_script_include.script:
function GetRelatedRecordsList(grRecord,bApplicationFilesOnly)
{
	var tree = GetRelatedRecordsTree(grRecord,bApplicationFilesOnly);
	if (tree === false)
	{
		return false;
	}

	// convert the tree into a list
	var aRelatedRecords = [];


	// AI generated code
	function isArrayLike(v)
	{
		if (!v)
		{
			return false;
		}
		if (typeof Array !== 'undefined' && typeof Array.isArray === 'function' && Array.isArray(v))
		{
			return true;
		}
		return (typeof v === 'object' && typeof v.length === 'number' && typeof v.splice === 'function');
	}

	function addChildren(node)
	{
		if (!node)
		{
			return;
		}

		var children = node._related_records || node.related_records || node._children;
		if (isArrayLike(children))
		{
			for (var i = 0; i < children.length; i++)
			{
				var child = children[i];
				addChildren(child);
				delete child._related_records;
				aRelatedRecords.push(child);

			}
		}
	}

	addChildren(tree);

	return aRelatedRecords;
}
