sys_script_include.script:
function Clone(vValue)
{
	var strType = GetType(vValue);
	if (strType == 'ScopedGlideElement')
	{
		return ''+vValue;
	}
	//Debug('Clone: '+RenderValue(vValue));
	if (IsObject(vValue) == true)
	{
		var oClone = {};

		var aKeys = Object.keys(vValue);
		//Debug('Clone: aKeys: '+RenderValue(aKeys));
		aKeys.forEach( function(strKey)
		{
			Debug('Clone: ['+strKey+']: '+RenderValue(vValue[strKey])+' GetType(): '+GetType(vValue[strKey]));
			oClone[strKey] = Clone(vValue[strKey]);
		});		

/*		
		var vClone = {};
        for (var prop in vInput)
		{
            if (vInput.hasOwnProperty(prop))
			{
                vClone[prop] = Clone(vInput[prop]);
			}
		}
        return vClone;
		*/
		return oClone;
	}
	if (strType == 'string')
	{
		return GetStringValue(vValue);
	}
	if (strType == 'String')
	{
		return GetStringValue(vValue);
	}
	if (strType == 'number')
	{
		return parseInt(vValue);
	}
	return vValue;
}
