sys_script_include.script:
function HtmlRenderScriptUrl(strUrl,bModule)
{
	strUrl = GetStringValue(strUrl);
	if (strUrl == '')
	{
		return '';
	}
	bModule = GetBoolValue(bModule);
	var strModule = '';
	if (bModule == true)
	{
		strModule = ' type="module"';
	}
	var strHtml = '<script src="'+strUrl+'"'+strModule+'></script>\n';
	return strHtml;
}
