sys_script_include.script:
function SetValue(vValue, vObject, vKey1, vKey2, vKey3, vKey4, vKey5, vKey6)
{
	if (IsObject(vObject) == false)
	{
		vObject = {};
	}
	if (vKey1 == undefined)
	{
		return vObject;
	}
	if (vKey2 == undefined)
	{
		vObject[vKey1] = vValue;
		return vObject;
	}
	if (IsObject(vObject[vKey1]) !=  true)
	{
		vObject[vKey1] = {};
	}
	vObject[vKey1] = SetValue(vValue,vObject[vKey1],vKey2,vKey3,vKey4,vKey5);
	return vObject;
}
