sys_script_include.script:
function HtmlRenderUnorderedList(aItems)
{
	var strHtml = '';
	aItems = GetArrayValue(aItems);
	if (aItems.length > 0)
	{
		strHtml += '<ul>\n';
		for (var nItem = 0; nItem < aItems.length; nItem++)
		{
			strHtml += '<li>';
			strHtml += GetStringValue(aItems[nItem]);
			strHtml += '</li>';
		}

		strHtml += '</ul>\n';
	}
	return strHtml;
}
