sys_script_include.script:
function HtmlRenderPageA4(strContent, fMarginTopBottomCm, fMarginLeftRightCm)
{
	strContent = GetStringValue(strContent);
	fMarginTopBottomCm = GetFloatValue(fMarginTopBottomCm);
	fMarginLeftRightCm  = GetFloatValue(fMarginLeftRightCm);

	var fWidthA4 = 21.0;
	var fHeightA4 = 29.7;

	var strHtml = '';
	strHtml += '<div style="\n'
		+'width: '+fWidthA4+'cm;\n'
		+'height: '+fHeightA4+'cm;\n'
		+'border: 0;\n'
		+'margin: 0;\n'
		+'padding: '+fMarginTopBottomCm+'cm '+fMarginLeftRightCm+'cm;\n'
		+'box-sizing: border-box;\n'
		+'">\n';
	strHtml += strContent;
	strHtml += '</div>';
	return strHtml;
}
