sys_script_include.script:
function BusinessRuleGetOperation(record)
{
	var strOperation = '';
	if (IsValidRecord(record) == true)
	{
		if (IsFunction(record.operation) == true)
		{
			strOperation = record.operation();
		}
		if (strOperation == null)
		{
			strOperation = 'query';
		}
	}
	else
	{
		strOperation = 'undefined';
	}      
	return strOperation;
}
