sys_script_include.script:
function HtmlRenderRelatedRecords(grRecord,strMode,nMaxLevel)
{
	strMode = GetStringValue(strMode);
	if (strMode == '')
	{
		strMode = 'tree';
	}

	nMaxLevel = GetIntegerValue(nMaxLevel);
	if (nMaxLevel == 0)
	{
		nMaxLevel = 5;
	}


	var strHtml = '';
	var bError = false;

	if (IsValidRecord(grRecord) == false)
	{
		strHtml += HtmlRenderError('Invalid Record!');
		return strHtml;
	}


	function RenderNode(node,nLevel)
	{
		var grNode = GetRecord(node.sys_class_name,node.sys_id);
		var strRecordUrl = GetRecordPath(node.sys_class_name,node.sys_id);

		var strContent = '';
		if (IsValidRecord(grNode) == true)
		{
			strContent += '<b>' + RenderRecordSummary(grNode) + '</b><br/>'; 
		}
		else
		{
			var strName = GetStringValue(node.name);
			var strClassName = node.sys_class_name;
			strContent += '<b>' + strClassName + ': ' + strName + '</b><br/>';
		}
		strContent += HtmlRenderLink(strRecordUrl,'Open Record',true);
		strContent += '<br/>';

		var strStatus = GetStringValue(GetValue(node,'_related_records_status'));
		if (strStatus != '')
		{
			strContent += HtmlRenderError(strStatus);
		}

/*		if (nLevel != 0)
		{
			strContent += ' | ';
			var strTreeUrl = '/x_snc_devtools_flow.do?flow=' + node.sys_id + '&maxlevel='+nMaxLevel;
			strContent += HtmlRenderLink(strTreeUrl,'Show Tree',true);
		}
		*/

		return HtmlRenderRoundedBox(strContent);
	}



	if (strMode == 'tree')
	{
		var tree = GetRelatedRecordsTree(grRecord,nMaxLevel);
		if (tree == false)
		{
			strHtml += HtmlRenderError('Could not load related record tree!');
			return strHtml;
		}
		strHtml += HtmlRenderTree([tree],0,false,RenderNode,'_related_records');

		if (AppGetProperty('logging.verbosity') == 'debug')
		{
			strHtml += HtmlRenderValue(tree);
		}
	}
	else if (strMode == 'list')
	{
		var list = GetRelatedRecordsList(grRecord,nMaxLevel);
		if (list == false)
		{
			strHtml += HtmlRenderError('Could not load related record list!');
			return strHtml;
		}
		strHtml += HtmlRenderTree(list,0,false,RenderNode,'_related_records');

		if (AppGetProperty('logging.verbosity') == 'debug')
		{
			strHtml += HtmlRenderValue(list);
		}
	}
	else
	{
		strHtml += HtmlRenderError('Unsupported display mode!');
		return strHtml;
	}

	return strHtml;
}
