sys_script_include.script:
function SourceControlApplyRemoteChanges(vApp,bAsync)
{
	var grApp = GetAppRecord(vApp);
	if (IsValidRecord(grApp) == false)
	{
		return false;
	}
	bAsync = GetBoolValue(bAsync);

	SetCurrentScope(grApp.sys_id);

	var aic = sn_vcs.AppSourceControl.applyIncomingChanges();
	aic.setSysApp(grApp.sys_id);
	aic.setStashMessage('Applying remote changes.');
	aic.setExecuteEvenIfHasNoIncomingChanges(true)
	aic.setExecuteEvenIfHasOutgoingChanges(true)
	aic.setDiskDiff(false);
	var strProgressSysId = aic.start();

	if (bAsync == true)
	{
		return strProgressSysId;
	}

	WaitForWorker(strProgressSysId,2);

	var grProgress = GetRecord('sys_progress_worker',strProgressSysId);
	if (grProgress != false)
	{
		var strStateCode = GetStringValue(grProgress.state_code);  
		if (strStateCode != 'success')
		{
			return false;
		}
	}
	return true;
}
