sys_script_include.script:
function RedirectToRecord(grRecord,strView)
{
	if (IsValidRecord(grRecord) == false)
	{
		return false;
	}
	strView = GetStringValue(strView);
	var strViewParam = '';
	if (strView != '')
	{
		strViewParam = '&sysparm_view='+strView;
	}
	gs.setRedirect(grRecord.getRecordClassName()+'.do?sys_id='+grRecord.sys_id+strViewParam);
	return true;
}
