sys_script_include.script:
function AppBuilderAclInsertOrUpdate(vScope,strType,strOperation,strName,strDecisionType,strDescription,aRoles,strScript)
{
	var grScope = GetAppRecord(vScope);
	if (IsValidRecord(grScope) == false)
	{
		return false;
	}
	strType = GetStringValue(strType);
	if (strType == '')
	{
		return false;
	}
	strOperation = GetStringValue(strOperation);
	if (strOperation == '')
	{
		return false;
	}
	strName = GetStringValue(strName);
	if (strName == '')
	{
		return false;
	}
	strDecisionType = GetStringValue(strDecisionType);
	if (strDecisionType == '')
	{
		return false;
	}
	strDescription = GetStringValue(strDescription);
	aRoles = GetArrayValue(aRoles);
	strScript = GetStringValue(strScript);
	var bAdvanced = false;
	if (strScript != '')
	{
		bAdvanced = true;
	}
	var strAclSysId = RecordInsertOrUpdate(
		'sys_security_acl',
		{
			sys_scope: GetStringValue(grScope.sys_id),
			type: strType,
			operation: strOperation,
			name: strName,
			description: strDescription 
		},
		{
			// insert
		},
		{
			// update
			advanced: bAdvanced,
			script: strScript
		});

	if (IsValidSysId(strAclSysId) == false)
	{
		return false;
	}
	for (var nRole = 0; nRole < aRoles.length; nRole++)
	{
		var vRole = aRoles[nRole];
		var grRole = GetRoleRecord(vRole);
		if (IsValidRecord(grRole) == true)
		{
			var strAclRoleSysId = RecordInsertOrUpdate(
				'sys_security_acl_role',
				{
					sys_scope: GetStringValue(grScope.sys_id),
					sys_security_acl: strAclSysId,
					sys_user_role: GetStringValue(grRole.sys_id)
				},
				{
					// insert
				},
				{
					// update
				});
			if (IsValidSysId(strAclRoleSysId) == false)
			{
				LogError(
					'AppBuilderAclInsertOrUpdate: Failed to insert or update role to ACL: '+strAclSysId);
			}
		}
	}

	return strAclSysId;
}
