sys_script_include.script:
function HtmlRenderPageRelatedRecords()
{
	var strSysId = GetParam('record');
	var strTable = GetParam('table');
	var strMode = GetParam('mode');

	if (strMode == '')
	{
		strMode = 'tree';
	}

	var strHtml = '';

	strHtml += '<h1>DevTools - Related Records</h1>';

	var grRecord = GetRecord(strTable,strSysId);
	if (IsValidRecord(grRecord) == false)
	{
		strHtml += HtmlRenderError('Could not load record!');
		return strHtml;
	}	

	strHtml += HtmlRenderTabNavigation(
		[
			{
				id: 'tree',
				text: 'Tree',
				url: '?table='+strTable+'&record='+strSysId+'&mode=tree'
			},
			{
				id: 'list',
				text: 'List',
				url: '?table='+strTable+'&record='+strSysId+'&mode=list'
			}
		],
		strMode);

	strHtml += '<div style="padding-top: 10px;">';
		strHtml += HtmlRenderRelatedRecords(grRecord,strMode);
	strHtml += '</div>';

	return strHtml;
}
