sys_script_include.script:
function GetUniqueEmailsFromString(strInput)
{
	strInput = GetStringValue(strInput);

	var aEmails = [];

	var aRaw1 = strInput.split(';');
	var aRaw2 = [];
	for (var nRaw1 = 0; nRaw1 < aRaw1.length; nRaw1++)
	{
		var strRaw = aRaw1[nRaw1];
		aRaw = 	strRaw.split('\n');
		for (var nRaw = 0; nRaw < aRaw.length; nRaw++)
		{
			aRaw2.push(aRaw[nRaw]);
		}
	}
	for (var nRaw2 = 0; nRaw2 < aRaw2.length; nRaw2++)
	{
		var strRaw = aRaw2[nRaw2];
		var nLowerThan = strRaw.indexOf('<');
		if (nLowerThan != -1)
		{
			strRaw = strRaw.substring(nLowerThan+1);
		}
		var nGraterThan = strRaw.indexOf('>');
		if (nGraterThan != -1)
		{
			strRaw = strRaw.substring(0,nGraterThan);
		}
		strRaw = strRaw.trim();
		strRaw = strRaw.toLowerCase();

		if (IsValidEmail(strRaw))
		{
			aEmails = ArrayPushUnique(aEmails,strRaw);
		}
	}
	aEmails = aEmails.sort();
	return aEmails;
}
