sys_script_include.script:
function GetCountryRecord(strValue)
{
	strValue = GetStringValue(strValue);

	if (strValue == '')
	{
		return false;
	}


	var grCountry = new GlideRecord('core_country');

	if (IsValidSysId(strValue))
	{
		if (grCountry.get(strValue))
		{
			return grCountry;
		}
		return false;
	}

	var grCountry = new GlideRecord('core_country');
	grCountry.addQuery('name','=',strValue);
	grCountry.query();
	if (grCountry.next())
	{
		return grCountry;
	}

	var grCountry = new GlideRecord('core_country');
	grCountry.addQuery('iso3166_3','=',strValue);
	grCountry.query();
	if (grCountry.next())
	{
		return grCountry;
	}	

	return false;
}
