sys_script_include.script:
function SyncDependency(grDependency)
{
	if (IsValidRecord(grDependency) == false)
	{
		return false;
	}
	if (grDependency.getRecordClassName() != 'x_snc_devtools_dependency')
	{
		return false;
	}



	//var strMinVersion = GetStringValue(grDependency.min_version);
	//var strVersions = GetStringValue(grDependency.x_snc_devtools_versions);
	var strName = GetStringValue(grDependency.x_snc_devtools_name);

	var grPackage = GetRecord('sys_package',grDependency.dependency);

	if (strName == '')
	{
		if (IsValidRecord(grPackage) == true)
		{
			grDependency.name = GetCleanAppName(grPackage.name);	
		}
	}

	/*
	if (strMinVersion == '' && strVersions == '')
	{
		if (IsValidRecord(grPackage) == true)
		{
			strMinVersion = GetStringValue(grPackage.version);
			if (strMinVersion != '')
			{
				grDependency.min_version = strMinVersion;
			}
		}
	}

	if (strVersions == '')
	{
		grDependency.x_snc_devtools_versions = strMinVersion;
	}

	if (strMinVersion == '')
	{
		var aVersions = GetArrayValue(strVersions);
		grDependency.min_version = GetValue(aVersions,0);
	}
	*/

	return grDependency;
}
