sys_script_include.script:
function LoadMessages(strApplication)
{
	var text = {};
	var grMessages = new GlideRecord('sys_ui_message');
	grMessages.addQuery('application',strApplication);
	grMessages.addQuery('language','English');
	grMessages.query();
	while (grMessages.next())
	{
		gs.info(grMessage.key+' '+grMessage.message);
		text[grMessage.key] = grMessage.message;
	}
	return text;
}
