sys_script_include.script:
function GetRolesFromUser(strUserSysId)
{
	strUserSysId = x_snc_devtools.GetStringValue(strUserSysId);
	var aRoles = [];
	var grUser = new GlideRecord('sys_user');
	if (grUser.get(strUserSysId) == false)
	{
		return aRoles;
	}
	var grUserHasRole = new GlideRecord('sys_user_has_role');
	grUserHasRole.addQuery('user','=',strUserSysId);
	grUserHasRole.addQuery('state','=','active');
	grUserHasRole.query();
	while(grUserHasRole.next())
	{
		var strRoleSysId = GetStringValue(grUserHasRole.getValue('role'));
		if (strRoleSysId != '')
		{
			aRoles.push(strRoleSysId);
		}
	}
	return aRoles;
}
