sys_script_include.script:
function AppBuilderMoveToScope(grRecord, vScope, bMoveFromAllScopes, bMoveDataModel)
{
	if (IsValidRecord(grRecord) == false)
	{
		return false;
	}
	if (IsDerivedFromTable(grRecord.getRecordClassName(),'sys_metadata') == false)
	{
		LogWarning('AppBuilderMoveToScope: the given record is not derived from sys_metadata:\n'
				+ RenderRecordSummary(grRecord));
		return false;
	}
	var grScope = GetRecord('sys_scope',vScope);
	if (IsValidRecord(grScope) == false)
	{
		LogWarning('AppBuilderMoveToScope: the given target scope does not exist:\n'
				+ RenderValue(vScope));		
		return false;
	}

	var aRelatedRecords = GetRelatedRecordsList(grRecord,true);
	if (aRelatedRecords == false)
	{
		return false;
	}

	bMoveFromAllScopes = GetBoolValue(bMoveFromAllScopes);

	for (var nRelatedRecord = 0; nRelatedRecord < aRelatedRecords.length; nRelatedRecord++)
	{
		var related_record = aRelatedRecords[nRelatedRecord];
		var grRelatedRecord = GetRecord(related_record.sys_class_name,related_record.sys_id);
		if (IsValidRecord(grRelatedRecord) == false)
		{
			LogError('AppBuilderMoveToScope: The following record is not valid:\n'
				+ RenderRecordSummary(grRelatedRecord));
			return false;
		}
		if (IsOotbRecord(grRelatedRecord) == true)
		{
			LogError('AppBuilderMoveToScope: The following record is an OOTB record which must not be moved:\n'
				+ RenderRecordSummary(grRelatedRecord));
			return false;
		}
	}

	var aDataModelTables = [
		'sys_db_object',
		'sys_dictionary'
		];


	for (var nRelatedRecord = 0; nRelatedRecord < aRelatedRecords.length; nRelatedRecord++)
	{
		var related_record = aRelatedRecords[nRelatedRecord];
		var grRelatedRecord = GetRecord(related_record.sys_class_name,related_record.sys_id);

		var bSameScope = GetBoolValue(grRecord.sys_scope == grRelatedRecord.sys_scope);

		if ( bSameScope == true || bMoveFromAllScopes == true)
		{
			if (
					bMoveDataModel == true
				||	ArrayValueExists(aDataModelTables,related_record.sys_class_name) == false
				)
			{
				grRelatedRecord.sys_scope = grScope.sys_id;
				grRelatedRecord.sys_package = grScope.sys_id;
				GlideRecordUpdate(grRelatedRecord,true);
				Log('AppBuilderMoveToScope: Moved record:\n' + RenderRecordSummary(grRelatedRecord) + '\nto scope: '+ grScope.sys_id);
			}
			else
			{
				LogWarning('AppBuilderMoveToScope: Ignored data model record:\n'
					+ RenderRecordSummary(grRelatedRecord));
			}

		}
		else
		{
			LogWarning('AppBuilderMoveToScope: Ignored record:\n' + RenderRecordSummary(grRelatedRecord));
		}
	}

	return true;
}
