sys_script_include.script:
function GetTableFromSysId(strSysId)
{
	if (IsValidSysId(strSysId) == false)
	{
		return false;
	}

	var anomalies = GetSysIdAnomalies();
	var strTable = GetStringValue(GetValue(anomalies,strSysId));
	if (strTable != '')
	{
		return strTable;
	}

	var aPriorityTables = [
		'sys_documentation', // sys_documentation records don't have a class name
		'sys_metadata',
		'sys_package',
		'domain',
		'task',
		'sys_user',
		'sys_user_group',
		'cmdb_ci',
		'core_company',
		'alm_asset',
		'ast_contract',
		'scan_result',
		'sys_atf_test_result',
		'sys_atf_test_suite_result',
		'sys_attachment',
		'sys_translated_text',
		'sys_progress_worker',
		'discovery_credentials',
		'sys_connection',
		'kb_knowledge',
		'kb_knowledge_base'
	];

	var aIgnoreTablesByPrefix = [
		'ts_', 
		'pa_scores_',
		'tmp_',
		'v_',
		'm2m_',
		'_m2m',
		'_mtom',
		'000',
		'sn_hr_',
		'sn_doc_',
		'sys_rollback_incremental',
		'sys_rollback_sequence',
		'sn_templated_snip_note_template',
		'sn_templated_snip_channel',
		'pa_favorites',
		'pa_migration_ignored_scores',
		'cmdb_ire_partial_payloads_index'
	];	

	for (var nTable = 0; nTable < aPriorityTables.length; nTable++)
	{
		var grRecord = GetRecord(aPriorityTables[nTable],strSysId,true);
		if (grRecord != false)
		{
			return GetStringValue(grRecord.getRecordClassName());
		}
	}

	var aRemainingTables = [];

	var grTables = new GlideRecord('sys_db_object');
	grTables.orderByDesc('name'); // to check tables with x_ and u_ first
	grTables.addQuery('extension_model'); // only tables that don't extend from anything
	grTables.addQuery('super_class','=',''); // No derived classes
	var strEncodedQuery = 'sys_update_nameISNOTEMPTY';
	strEncodedQuery += '^nameISNOTEMPTY';

	for (var nTable = 0; nTable < aIgnoreTablesByPrefix.length; nTable++)
	{
		var strPrefix = aIgnoreTablesByPrefix[nTable];
		strEncodedQuery += '^nameNOT LIKE' + strPrefix;

	}
	grTables.addEncodedQuery(strEncodedQuery);

	for (var nPriorityTable = 0; nPriorityTable < aPriorityTables.length; nPriorityTable++)
	{
		grTables.addQuery('name','!=',aPriorityTables[nPriorityTable]);
	}
	GlideRecordQuery(grTables,true);
	while (grTables.next())
	{
		var strTable  = grTables.getValue('name');
		aRemainingTables.push(strTable);
	}

	for (var nTable = 0; nTable < aRemainingTables.length; nTable++)
	{
		var grRecord = GetRecord(aRemainingTables[nTable],strSysId,false);
		if (grRecord != false)
		{
			return GetStringValue(grRecord.getRecordClassName());
		}
	}	

	return false;
}
