sys_ui_action.script:
var bDoUpdate = true; 
var bUiScriptSetGlobal = false;
if (current.getRecordClassName() == 'sys_ui_script')
{
	if (current.sys_scope != 'global')
	{
		if (GetStringValue(current.global) == 'true')
		{
			current.global = false;
			bUiScriptSetGlobal = true;
		}
	}
}

if (bDoUpdate == true)
{
	if (current.isNewRecord() == true)
	{
		GlideRecordInsert(current,IsGhostRequiredForRecordUpdate(current));
		// Taken from the OOTB Save context menu item:
		gs.include('ActionUtils');
		var au = new ActionUtils();
		au.postInsert(current);	
	}
	else
	{
		GlideRecordUpdate(current,IsGhostRequiredForRecordUpdate(current));
	}
}

if (bUiScriptSetGlobal == true)
{
	gs.addErrorMessage(
		HtmlEncode(
			'Global UI Scripts in scoped applications are not allowed. The necessary change is prevented by the "Access Handler".\n'
			+'Refer to https://support.servicenow.com/kb?id=kb_article_view&sysparm_article=KB0722958\n'
			+'But you can set the global value running the following background script:\n'
			+'x_snc_devtools.RecordSetValue("sys_ui_script","'+current.sys_id+'","global","true",true);'));
	//gs.addInfoMessage(RecordSetValue('sys_ui_script',current.sys_id,'global','true',true));
}

RedirectToRecord(current);
