function GetElementById(strId)
{
	console.log('GetElementById("'+strId+'")');

	var element = null;

	element = top.document.getElementById(strId);
	if (element != null)
	{
		console.log('top.document.getElementById');
		return element;
	}

/*
	var elementForm = g_form.getFormElement();
	if (elementForm != null)
	{
		console.log('elementForm: '+RenderValue(elementForm));
		element = elementForm.getElementById(strId);
		if (element != null)
		{
			console.log('g_form.getFormElement().getElementById');
			return element;
		}
	}
*/



	var aFrames = top.document.getElementsByTagName('iframe');

	//console.log('aFrames: '+RenderValue(aFrames));
	for (var nFrame = 0; nFrame < aFrames.length; nFrame++)
	{
		var elementFrame = aFrames[nFrame];
		//console.log('Iterating over iframes: '+RenderValue(elementFrame));

		if (elementFrame.getAttribute('id') == 'gsft_main')
		{
			element = elementFrame.contentWindow.document.getElementById(strId);	
			if (element != null)
			{
				console.log('elementFrame.contentWindow.document.getElementById');
				return element;
			}
		}
	}

	var elementForm = g_form.getFormElement();
	if (elementForm != null)
	{
		elements = elementForm.querySelectorAll("#"+strId);
		//console.log('elements: '+RenderValue(elements));

		element = GetValue(elements,0);	
		if (element != null)
		{
			console.log('elementForm.querySelectorAll');
			return element;
		}
	}

	if ($ !== undefined)
	{
		element = $(strId);
		if (element != null)
		{
			console.log('$()');
			return element;
		}
	}


	return element;
}
