function GetElementById(strId)
{
	var element = null;

	element = top.document.getElementById(strId);
	if (element != null)
	{
		return element;
	}

/*
	var elementForm = g_form.getFormElement();
	if (elementForm != null)
	{
		console.log('elementForm: '+RenderValue(elementForm));
		element = elementForm.getElementById(strId);
		if (element != null)
		{
			console.log('g_form.getFormElement().getElementById');
			return element;
		}
	}
*/



	var aFrames = top.document.getElementsByTagName('iframe');
	for (var nFrame = 0; nFrame < aFrames.length; nFrame++)
	{
		var elementFrame = aFrames[nFrame];
		if (elementFrame.getAttribute('id') == 'gsft_main')
		{
			element = elementFrame.contentWindow.document.getElementById(strId);	
			if (element != null)
			{
				return element;
			}
		}
	}

	var elementForm = g_form.getFormElement();
	if (elementForm != null)
	{
		elements = elementForm.querySelectorAll("#"+strId);
		element = GetValue(elements,0);	
		if (element != null)
		{
			return element;
		}
	}

	if ($ !== undefined)
	{
		element = $(strId);
		if (element != null)
		{
			return element;
		}
	}


	return element;
}
