sys_ui_script.script:
function DoModalPrompt(strTitle, strText, strDefaultValue, callbackResult)
{
	// TOOD: https://davidmac.pro/posts/2022-02-08-glidemodal/

	var bCallback = false;
	if (typeof callbackResult == 'function')
	{
		bCallback = true;
	}	

	var Dialog = GetGlideDialogClass();
	if (Dialog != null && bCallback)
	{
		strTitle = GetStringValue(strTitle);
		if (strTitle == '')
		{
			strTitle = '&#160;';
		}
		var dialog = new Dialog("glide_prompt", false);
		dialog.setTitle(strTitle);
		dialog.setPreference("title", strText); // yes, "title" is the actual text...

		function onOK(strValue)
		{
			callbackResult(strValue);
			GlideModal.get().destroy();
		}

		function onCancel(strValue)
		{
			callbackResult(false);
			GlideModal.get().destroy();
		}

		dialog.setPreference('onPromptComplete',onOK);
		dialog.setPreference('onPromptCancel',onCancel);
		dialog.render();
		return;
	}	


	// Fallback
	strTitle = GetStringValue(strTitle);
	if (strTitle != '')
	{
		strText = strTitle + '\n\n' + strText;
	}
	var strResult = prompt(strText,strDefaultValue);
	if (bCallback == true)
	{
		callbackResult(strResult);
		return;
	}
	return strResult;
}
